import React, { useEffect } from 'react'
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import { useNavigate } from 'react-router-dom'
import { useState } from 'react';

function NotifModal() {
    const user = JSON.parse(localStorage.getItem("user"))
    const [show, setShow] = useState(false)
    const [notifs, setNotifs] = useState([])
    const [notifParams, setNotifParams] = useState([])

    let navigate = useNavigate()


    const handleShow = () => setShow(true)
    const handleClose = () => setShow(false)

    useEffect(() => {
        fetch(`/user/notifications/${user.id}`)
        .then(res => res.json())
        .then(data => {
            setNotifs(data)
            setNotifParams(data.recipient_id)
            console.log();
        })

    }, [])

    console.log(notifs);
    console.log(notifParams);

    const newArray = []
    const mapArray = []

    notifs?.forEach(function (e) {
        let x = e.params
        newArray.push(x)
    })

    newArray?.forEach(function (e) {
        let c = e.comment
        c.ticket = e.ticket.title
        mapArray.push(c)
    })
    console.log(mapArray);

  return (
    <div>
        <a class="dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">

            <span class=" badge rounded-pill bg-danger">

            {mapArray.count >= 9 ? "9+" : mapArray.count}  <i class="bi bi-heart-fill"></i>
            </span>
            <ul className='dropdown-menu'>
            {mapArray?.map(n => {
                    return( 
                        <div className='border'>
                        <li className='dropdown-item' onClick={() => navigate(`/TicketDetails/${n.ticket_id}`)}>New Comment on {n.ticket}</li>
                        </div>
                    )
                })}

            </ul>

            </a>

    </div>
  )
}

export default NotifModal
