import React from 'react'
import { useState } from 'react'
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';


function CreateProjectModal() {
    const [show, setShow] = useState(false)
    const [title, setTitle] = useState("")
    const [descrip, setDescrip] = useState("")
    const handleShow = () => setShow(true)
    const handleClose = () => setShow(false)


    function handleNewProject(e) {
        e.preventDefault()

        const formData = new FormData()
        formData.append("title", title)
        formData.append("description", descrip)

        fetch('/projects', {
            method: 'POST',
              body: formData
            })
            .then(res => {
                if (res.ok) {
                    res.json()
                    .then(data => {
                        console.log(data)
                        setShow(false)
                    })
                }
            })

    }


   const handleTitleChange = e => {
        setTitle(e.target.value)
    }

   const handleDescripChange = e => {
        setDescrip(e.target.value)
    }



  return (
    <div>
        <Button className='btn btn-outline-primary' style={{width: 200}} onClick={handleShow}>
                <p>Add Project</p>
        </Button>

        <Modal show={show} onHide={handleClose} centered style={{ textAlign: 'center' }}> 

        <Modal.Header closeButton>
            <Modal.Title>Create New Project</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <div>
                <form onSubmit={handleNewProject}>
                    <label className='form-label'></label>
                    <input onChange={handleTitleChange} type="text" className='modal-edit-control'
                    placeholder='Title'
                    value={title}>
                    </input>

                    <label className='form-label'></label>
                    <input onChange={handleDescripChange} type="text" className='modal-edit-control'
                    placeholder='Description'
                    value={descrip}>
                    </input>

                    <button type='submit'></button>
                </form>
            </div>
        </Modal.Body>
        </Modal>
    </div>
  )
}

export default CreateProjectModal
