import React from 'react'
import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'
import ManNavBar from '../../components/ManNavBar';

function ManTicketDetails() {
    const user = JSON.parse(localStorage.getItem("user"))
    let params = useParams()

    const [ticket, setTicket] = useState([])
    const [message, setMessage] = useState('')
    const [errors, setErrors] = useState('')
    const [newStatus, setNewStatus] = useState('')
    const [newPriority, setNewPriority] = useState('')
    const [newTypeOf, setNewTypeOf] = useState('')
    const [newDeveloper, setNewDeveloper] = useState('')
    const [ticMan, setTicMan] = useState([])
    const [ticDev, setTicDev] = useState([])
    const [ticComments, setTicComments] = useState([])
    const reload=()=>window.location.reload();

    const userSend = []

    userSend.push(user.id, user.username, user.password_digest, user.first_name, user.last_name, user.email, user.account_type, user.developer_id, user.manager_id, user.admin_id, user.created_at, user.updated_at)

    useEffect(() => {
        fetch(`/ticket/details/${params.id}`)
        .then(res => res.json())
        .then(data => {
          console.log(data);
         setTicket(data)
         setTicMan(data.manager)
         setTicDev(data.developer)
         setNewStatus(data.status)
         setNewPriority(data.priority)
        })
     }, [])

     useEffect(() => {
        fetch(`/ticket/comments/${params.id}`)
        .then(res => res.json())
        .then(comments => {
            console.log(comments)
        setTicComments(comments)
        })
     }, [])


     console.log(user);

     function handleNewComment(e) {
         e.preventDefault()

        const formData = new FormData()
        formData.append("message", message)
        // formData.append("user_id", user.id)
        formData.append("ticket_id", params.id)
        formData.append("commentable_id", user.id)
        formData.append("commentable_type", user.account_type)
        // formData.append("commentable", userSend)

            fetch('/comments', {
                method: 'POST',
                body: formData
                })
                    .then(res => {
                        if (res.ok) {
                            res.json()
                            .then(data => {
                                console.log(data)

                            })
                        } else {
                            res.json()
                            .then(({errors}) => setErrors(errors))
                        }
                    })
     }

     function handleNewStatus(e) {
        e.preventDefault()

        const formData = new FormData()
        formData.append("status", newStatus)

        fetch(`/tickets/${params.id}`, {
            method: 'PATCH',
              body: formData
            })
            .then(res => {
                if (res.ok) {
                    res.json()
                    .then(data => {
                        console.log(data)
                        reload()
