import React from 'react'
import Navbar from '../../components/Navbar'
import CreateProjectModal from './CreateProjectModal';
import MiniProject from './MiniProject';
import { useState } from 'react';
import {DragDropContext, Droppable, Draggable} from 'react-beautiful-dnd'

function UserProjects({user, filteredProjects, setQuery}) {

  const handleQuery = e => {
    setQuery(e.target.value)
  }

console.log(user.projects);

function handleOnDragEnd(result) {
  const items = Array.from(filteredProjects)
  const [reordereditem] = items.splice(result.source.index, 1)
  items.splice(result.destination.index, 0, reordereditem)

  setProjects(items)
}

const [projects, setProjects] = useState(filteredProjects) 

  return (
    <div>
      <Navbar />
            <div class="input-group mb-2 justify-content-center mt-5">
              <div class="form-outline">
                <input onChange={handleQuery} placeholder='Search...' type="search" id="form1" class="form-control h-50 mt-5" />
                <label class="form-label" for="form1"></label>
              </div>
              <CreateProjectModal />
           </div>
      <div class="container border">
      <h1 className='mx-auto h3 pb-3'>{user.first_name}'s Projects</h1>
    <div class="row d-flex justify-content-center align-items-center h-100">
      <DragDropContext onDragEnd={handleOnDragEnd}>
        <Droppable droppableId={'projects'}>
          {(provided) => (
      <ul class="col col-xl-10 row " {...provided.droppableProps} ref={provided.innerRef}>
        {projects?.map((proj, index) => 
        <Draggable provided={provided} key={proj.id} draggableId={(proj.id).toString()} index={index} >
          {(provided) => (
            <li class="col-sm " {...provided.draggableProps} {...provided.dragHandleProps} ref={provided.innerRef}>
          <MiniProject proj={proj} user={user} />
          </li>
          )}
          </Draggable>
          )}
      </ul>
      )}
      </Droppable>
      </DragDropContext>
    </div>
  </div>
  </div>
  )
}

export default UserProjects
