import React from 'react'
import { Chart } from "react-google-charts";
import { useState, useEffect } from 'react';

function Piechart({user, tics}) {

    const [ticData, setTicData] = useState([])

  let data = [
      ["priority", "Amount"],
  ]

  tics.forEach(ticket => {
    data.push([ticket.priority, ticket.priority])
  });

  let numOfPriorities = []

  tics.forEach(ticket => {
    numOfPriorities.push(ticket.priority)
  });

  const createTally = numOfPriorities => {
        const tally= {}

        numOfPriorities.forEach(priority => {
            tally[priority] = tally[priority] ? tally[priority] + 1 : 1
        })
        return tally
  }

  console.log(createTally(numOfPriorities))


  const options = {
      title: "Tickets by Priority",

      backgroundColor: {
        fill: '#EFEDE7',
        stroke: '#b0b0b0',
        strokeWidth: 2
      },
  }


useEffect(() => {
    setTicData(data)
   }, [])

console.log(ticData)
console.log(numOfPriorities)



  return (
    <div>
        <Chart 
        chartType="PieChart"
        data={ticData}
        options={options}
        width={"100%"}
        height={"400px"}
        />
    </div>
  )
}

export default Piechart
