import React from 'react'
import Navbar from '../../components/Navbar'
import CreateProjectModal from './CreateProjectModal';
import MiniProject from './MiniProject';
import { useState } from 'react';
import {DragDropContext, Droppable, Draggable} from 'react-beautiful-dnd'

function UserProjects({user}) {

const [query, setQuery] = useState("") 

const handleQuery = e => {
  setQuery(e.target.value)
}

console.log(user.projects);

const filteredProjects = user.projects?.filter(singleProj => singleProj.title.toLowerCase().includes(query.toLowerCase()))

  return (
    <div>
      <Navbar />
            <div class="input-group mb-2 justify-content-center mt-5">
              <div class="form-outline">
                <input onChange={handleQuery} placeholder='Search...' type="search" id="form1" class="form-control h-50 mt-5" />
                <label class="form-label" for="form1"></label>
              </div>
              <CreateProjectModal />
           </div>
      <div class="container border">
      <h1 className='mx-auto h3 pb-3'>{user.first_name}'s Projects</h1>
    <div class="row d-flex justify-content-center align-items-center h-100">
      <DragDropContext>
        <Droppable droppableId='projects'>
          {(provided) => (
      <div class="col col-xl-10 row " {...provided.droppableProps} ref={provided.innerRef}>
        {filteredProjects?.map((proj, index) => 
        <Draggable provided={provided} key={proj.id} draggableId={(proj.id).toString()} index={index} >
          {(provided) => (
            <div  {...provided.draggableProps} {...provided.dragHandleProps} ref={provided.innerRef}>
          <MiniProject proj={proj} user={user} />
          </div>
          )}
          </Draggable>
          )}
      </div>
      )}
      </Droppable>
      </DragDropContext>
    </div>
  </div>
  </div>
  )
}

export default UserProjects
