import React from 'react'
import Navbar from '../../components/Navbar';
import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import MiniTicket from './MiniTicket';

function ProjectDetails({user}) {
  let navigate = useNavigate()
  let params = useParams()

  const [proj, setProj] = useState({
    manager: []
  })

useEffect(() => {
   fetch(`/project/alltickets/${params.id}`)
   .then(res => res.json())
   .then(data => {
     console.log(data);
    setProj(data)
   })
}, [])

console.log();


  return (
    <div className='conatiner'>
      <Navbar />
      <div className='row'>
      <div class="card w-50 mx-auto h-50">
        <div class="card-header">
          Project Details
         </div>
            <div class="card-body">
              <h5 class="card-title"></h5>
              <p class="card-text"><strong>Name: </strong>{proj.title}</p>
              <p><strong>Description: </strong>{proj.description}</p>
              {proj.manager.map(man => {
                return(
                  <p><strong>Manager: </strong>{man.first_name} {man.last_name}</p>
                )})}
              <a href="#" class="btn btn-primary btn-sm mt-3 ">Edit</a>
         </div>
      </div>
      <div class="card mb-5 mx-auto h-25" style={{width: 400}}>
              <div class="card-header">
                All {proj.title}'s Tickets
              </div>
              <ul class="list-group list-group-flush">
                {proj.tickets?.map(t => {
                    return(
                    <li class="list-group-item">{t.title}</li>
                )})}
              </ul>
            </div>
            </div>
      <div  class="container py-5 h-100 border mt-5">
        <div>
        </div>
          <div class="row d-flex justify-content-center align-items-center h-100">
              <div class="col col-xl-10 row ">
                {proj.tickets?.map(ticket => 
                  <MiniTicket key={ticket.id} ticket={ticket}/>)}
              </div>
          </div>
      </div>

    </div>
  )
}

export default ProjectDetails
