import React from 'react'
import { useState } from 'react'
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

function NewTicketModal({params}) {
    const [show, setShow] = useState(false)
    const [title, setTitle] = useState("")
    const [typeOf, setTypeof] = useState("")
    const [priority, setPriority] = useState("")
    const [descrip, setDescrip] = useState("")
    const handleShow = () => setShow(true)
    const handleClose = () => setShow(false)
    const reload=()=>window.location.reload();


    function handleNewTicket(e) {
        e.preventDefault()

        const formData = new FormData()
        formData.append("title", title)
        formData.append("description", descrip)
        formData.append("type_of", typeOf)
        formData.append("priority", priority)
        formData.append("project_id", params.id)

        fetch('/tickets', {
            method: 'PATCH',
              body: formData
            })
            .then(res => {
                if (res.ok) {
                    res.json()
                    .then(data => {
                        console.log(data)
                        setShow(false)
                        reload()
                    })
                }
            })


          }


    const handleTitleChange = e => {
        setTitle(e.target.value)
    }

   const handleDescripChange = e => {
        setDescrip(e.target.value)
    }

   const handleTypeOfChange = e => {
        setTypeof(e.target.value)
    }

   const handlePriorityChange = e => {
        setPriority(e.target.value)
    }



  return (
    <div>
    <Button className='btn btn-outline-primary ms-5 h-75 float-right' style={{width: 150}} onClick={handleShow}>
           <p>New Ticket</p>
   </Button>

   <Modal show={show} onHide={handleClose} centered style={{ textAlign: 'center' }} > 

<Modal.Header closeButton>
<Modal.Title>Update Project</Modal.Title>
</Modal.Header>
<Modal.Body>
<div>
   <form onSubmit={handleNewTicket}>
       <label className='form-label'></label>
       <input onChange={handleTitleChange} type="text" className='modal-edit-control me-5 border border-dark rounded'
       placeholder='Title'
       value={title}>
       </input>

       <label className='form-label'></label>
       <input onChange={handleDescripChange} type="text" className='modal-edit-control border border-dark rounded'
       placeholder='Description'
       value={descrip}>
       </input>

       <label className='form-label'></label>
       <select onChange={handleTypeOfChange} type="text" className='form-select-sm border me-5' style={{width: 100}}
       placeholder='Type of Bug'
       value={typeOf}>
           <option>Performance</option>
           <option>Security</option>
           <option>Functional</option>
           <option>Usability</option>
           <option>Syntax</option>
           <option>Compatability</option>
       </select>

       <label className='form-label'></label>
       <select onChange={handlePriorityChange} type="text" className='form-select-sm border' style={{width: 100}}
       placeholder='priority'
       value={priority}>
           <option>Low</option>
           <option>Medium</option>
           <option>High</option>
       </select>

       <button className='btn btn-outline-primary mt-5 h-75' type='submit'>Confirm Ticket</button>
   </form>
</div>
</Modal.Body>
</Modal>
</div>
  )
}

export default NewTicketModal
