import React, { useEffect } from 'react'
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import { useState } from 'react';

function NotifModal() {
    const user = JSON.parse(localStorage.getItem("user"))
    const [show, setShow] = useState(false)
    const [notifs, setNotifs] = useState([])
    const [notifParams, setNotifParams] = useState([])


    const handleShow = () => setShow(true)
    const handleClose = () => setShow(false)

    useEffect(() => {
        fetch(`/user/notifications/${user.id}`)
        .then(res => res.json())
        .then(data => {
            setNotifs(data)
            setNotifParams(data.recipient_id)
            console.log();
        })

    }, [])

    console.log(notifs);
    console.log(notifParams);

    const newArray = []
    const mapArray = []

    notifs?.forEach(function (e) {
        let x = e.params
        newArray.push(x)


    })

    newArray?.forEach(function (e) {
        let c = e.comment
        c.ticket = e.ticket.title

        mapArray.push(c)

    })
    console.log(mapArray);

  return (
    <div>
        <p onClick={handleShow}>
            <p>Notifications</p>
        </p>
        <Modal show={show} onHide={handleClose} centered style={{ textAlign: 'center' }}>
             <Modal.Header closeButton>
                <Modal.Title>Your Notifications</Modal.Title>
            </Modal.Header>
            <Modal.Body> 
                {mapArray?.map(n => {
                    return( 
                        <div className='border'>
                        <p>New Comment on {n.ticket}</p>
                        </div>
                    )
                })}
            </Modal.Body>

        </Modal>

    </div>
  )
}

export default NotifModal
