import React from 'react'
import Navbar from '../../components/Navbar'
import { useState } from 'react'

function UserProfile({user}) {

    const [firstName, setFirstName] = useState("")
    const [lastName, setLastName] = useState("")
    const [username, setUsername] = useState("")

    function handleProjectUpdate() {

        const formData = new FormData()
        formData.append("first_name", firstName)
        formData.append("last_name", lastName)
        formData.append("username", username)

        fetch(`/users/${user.id}`, {
            method: 'PATCH',
              body: formData
            })
            .then(res => {
                if (res.ok) {
                    res.json()
                    .then(data => {
                        console.log(data)
                    })
                }
            })


          }


    const handleFirstNameChange = e => {
        setFirstName(e.target.value)
    }

   const handleLastNameChange = e => {
        setLastName(e.target.value)
    }

   const handleUsernameChange = e => {
        setUsername(e.target.value)
    }

  return (
    <div>
        <Navbar />
        <div class="row"> 
             <div class="col-lg-4 mx-auto pt-5">
                 <div class="card mb-4"> 
                     <div class="card-body text-center"> 
                        <h5 class="my-3"><strong>{user.first_name} {user.last_name}</strong></h5>
                        <p class="text-muted mb-1">{user.account_type}</p>
                        <p class="text-muted mb-4">{user.bio}</p>
                        <div class="d-flex justify-content-center mb-2">

                        </div>
                     </div>
                 </div>
             </div>
        </div>
        <div class="col-lg-8 mx-auto w-50"> 
             <div class="card mb-4 ">
                     <div class="card-body"> 
                     <ul class="list-group list-group-flush">
                          <form>
                            <input class="list-group-item pb-4"><strong>Full Name :</strong> {user.first_name} {user.last_name} </input>

                             <p class="list-group-item pb-4"><strong>Email :</strong> {user.email}</p>

                             <p class="list-group-item pb-4"><strong>Username :</strong> {user.username}</p>

                             <p class="list-group-item pb-4"><strong>Account Type :</strong> {user.account_type}</p>
                             </form>
                        </ul>
                 </div>

             </div>
         </div>
     </div>
  )
}

export default UserProfile
